import React, { Component } from "react";
import "./List.css";

export default class List extends Component {
  render() {
    console.log(this);
    // 解构赋值，拿到props传过来的数据
    const { todo } = this.props;
    change=(state,id) => {
        return (e) => {
            if(state){ //如果鼠标划入
                this.setState({
                    [todoObj.id]:true
                })

            }else{
                this.setState({
                    [todoObj.id]:false
                })
            }
        }
        
        
    }
    
    return (
      <div className="listWrapper">
        {todo.map(todoObj => {
          return (
              
            <li key={todoObj.id} onMouseEnter={this.change(true,todoObj.id)}  onMouseLeave={this.change(false)}>
              <label>
                <input type="checkbox" />
                <span>{todoObj.name}</span>
              </label>
              <button className="btn btn-danger" style={{ display: "none" }}>
                删除
              </button>
            </li>
          );
        })}
      </div>
    );
  }
}
