import React, { Component } from "react";
import "./List.css";

export default class List extends Component {
  change = (state, id) => {
    return () => {
      if (state) {
        //如果鼠标划入
        this.setState({
          [id]: true
        });
      } else {
        this.setState({
          [id]: false
        });
      }
    };
  };

  componentWillMount() {
    //   在渲染吧前，先预存一个状态的默认值
    this.props.todo.map(todoObj => {
      this.setState({
        [todoObj.id]: false
      });
    });
  }
  render() {
    console.log(this);
    // 解构赋值，拿到props传过来的数据
    const { todo } = this.props;

    return (
      <div className="listWrapper">
        {todo.map(todoObj => {
          return (
            <li
              style={{
                backgroundColor: this.state[todoObj.id] ? "#eee" : "white"
              }}
              key={todoObj.id}
              onMouseEnter={this.change(true, todoObj.id)}
              onMouseLeave={this.change(false, todoObj.id)}
            >
              <label>
                <input type="checkbox" defaultChecked={todoObj.isDone} />
                <span>{todoObj.name}</span>
              </label>
              <button
                className="btn btn-danger"
                style={{
                  display: this.state[todoObj.id] ? "inline-block" : "none"
                }}
              >
                删除
              </button>
            </li>
          );
        })}
      </div>
    );
  }
}
