    // 根据id改变对象id的isDone
   let newTodoObj={
       id,
       isDone:checkState,
       nam
   }

    this.setState({
            todos:[...this.state.todos,newTodoObj]
    })
}




  state = {
    todos: []
  };

  render() {
    const { todos } = this.state; //结构赋值
    return (
      <div className="wrapper">
        <Header transform={this.addItem}  changeCheck={this.changeCheck} />
        <List todo={todos} />
        <Footer />
      </div>
    );
  }
}
